  def P001 = {
    (0 to 1000).filter(i => (i % 3 == 0 || i % 5 == 0)).sum
  }

  def P002 = {
    lazy val fibs: Stream[Int] = 0 #:: fibs.scanLeft(1)(_ + _)
    fibs.takeWhile(_ < 4000000).filter(_ % 2 == 0).sum
  }

  def P003 = {
    def isPrime(i: Long): Boolean = (2 to math.sqrt(i).toInt).takeWhile(i % _ != 0).length == math.sqrt(i).toInt - 1
    val num = 600851475143L;
    val aFactors = (2L to math.sqrt(num).toLong).filter(num % _ == 0)
    (aFactors ++ aFactors.map(num / _)).filter((i: Long) => isPrime(i)).max
  }

  def P004 = {
    def isPalindrome(num: Int): Boolean = num.toString() == num.toString().reverse
    (for (i <- 100 to 999; j <- 100 to 999) yield i * j).filter(isPalindrome).max
  }

  def P005 = {
    def isValid(num: Int): Boolean = ((2 to 20).takeWhile(num % _ == 0).length) == ((20 - 2) + 1)
    var num = 20;
    while (!isValid(num)) num += 20
    num
  }

  def P006 = {
    val numbers = (1 to 100)
    (math.pow(numbers.sum, 2) - numbers.map(math.pow(_, 2)).sum).toLong
  }

  def P007 = {
    def isPrime(num: Int): Boolean = ((2 to math.sqrt(num).toInt).takeWhile(num % _ != 0).length == (math.sqrt(num).toInt - 2) + 1)
    var index = 1
    var counter = 1
    while (counter < 10001) {
      index += 2
      if (isPrime(index)) counter += 1
    }
    index
  }

