  def P009 = {
    import scala.util.control.Breaks._

    def isPytagorial(a: Int, b: Int, c: Int): Boolean = (math.pow(a, 2) + math.pow(b, 2)) == (math.pow(c, 2))
    var res = 0
    breakable {
      for (a <- 1 to 998)
        for (b <- 1 to 998 - a) {
          val c = 1000 - a - b
          if (isPytagorial(a, b, c)) {
            res = (a * b * c)
            break
          }
        }
    }
    res
  }

