  def P012 = {
    def countDivisors(num: Long): Int = ((1 to math.sqrt(num).toInt).filter(num % _ == 0).length * 2)
    def triangleNumber(index: Int): Long = (1 to index).sum // (index*(index+1))/2 will be the mathematical way
    var index = 1
    while (countDivisors(triangleNumber(index)) <= 500) index += 1
    triangleNumber(index)
  }

