  def P014 = {
    def getSequenceLength(start: Long): Int = if (start == 0) 0 else if (start == 1) 1 else if (start % 2 == 0) 1 + getSequenceLength(start / 2) else 1 + getSequenceLength(3 * start + 1)
    Array.tabulate(1000000)((index: Int) => (getSequenceLength(index), index)).max
  }

  def P015 = {
    var map: Map[(Int, Int), Long] = Map()
    def calculateRoutes(m: Int, n: Int): Long = {
      if (m == 0 || n == 0) 1
      else if (map.contains(m, n)) map((m, n))
      else if (map.contains(n, m)) map(n, m)
      else {
        val res = (0 to n).toList.map(i => calculateRoutes(m - 1, n - i)).sum
        map += ((m, n) -> res)
        res
      }
    }
    calculateRoutes(20, 20)
  }

  def P016 = {
