  }

  def P017 = {
    val keyMap: Map[Int, Int] = Map(0 -> 0, 1 -> 3, 2 -> 3, 3 -> 5, 4 -> 4, 5 -> 4, 6 -> 3, 7 -> 5, 8 -> 5, 9 -> 4, 10 -> 3, 11 -> 6, 12 -> 6, 13 -> 8, 14 -> 8, 15 -> 7, 16 -> 7, 17 -> 9, 18 -> 8, 19 -> 8, 20 -> 6, 30 -> 6, 40 -> 5, 50 -> 5, 60 -> 5, 70 -> 7, 80 -> 6, 90 -> 6, 100 -> 7, 1000 -> 8)
    def count(num: Int): Int = num.toString.length match {
      case 1 => keyMap(num)
      case 2 => {
        if (num % 10 == 0) keyMap(num.toInt) else if (num < 20) keyMap(num.toInt) else keyMap(num.toString.charAt(0).asDigit * 10) + keyMap(num.toString.charAt(1).asDigit)
      }
      case 3 => {
        if (num.toString.substring(1).toInt > 0) (keyMap(num.toString.charAt(0).asDigit) + keyMap(100) + 3 + count(num.toString.substring(1).toInt)) else (keyMap(num.toString.charAt(0).asDigit) + keyMap(100))
      }
      case 4 => (keyMap(num.toString.charAt(0).asDigit) + keyMap(1000) + count(num.toString.substring(1).toInt))
      case _ => 0
    }
    (1 to 1000).map(count).sum
  }

  def P018 = {
    def getMax(pyramid: List[Array[Int]]): Int = {
      var nPyramid = pyramid
      for (a <- (1 to pyramid.length - 1)) {
        val bot = nPyramid(nPyramid.length - 1)
        val top = nPyramid(nPyramid.length - 2)
        for (j <- (0 to top.length - 1)) {
          top(j) = top(j) + (bot(j) max bot(j + 1))
        }
        nPyramid = nPyramid.take(nPyramid.length - 2) :+ top
      }
      nPyramid(0)(0)
    }
    val p = "75\n95 64\n17 47 82\n18 35 87 10\n20 04 82 47 65\n19 01 23 75 03 34\n88 02 77 73 07 63 67\n99 65 04 28 06 16 70 92\n41 41 26 56 83 40 80 70 33\n41 48 72 33 47 32 37 16 94 29\n53 71 44 65 25 43 91 52 97 51 14\n70 11 33 28 77 73 17 78 39 68 17 57\n91 71 52 38 17 14 91 43 58 50 27 29 48\n63 66 04 68 89 53 67 30 73 16 69 87 40 31\n04 62 98 27 23 09 70 98 73 93 38 53 60 04 23\n".split('\n').map(_.split(' ').map(_.toInt)).toList
    getMax(p)
  }

  def P019 = {
    val monthValues = List(0, 31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31)
    val leapMonthValues = List(0, 31, 29, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31)
    def countFirstOfMonthSundays(year: Int) = {
      val firstDay = (5 + year + (year - 1) / 4 - (1000 to (year - 1) by 1000).filter(_ % 400 != 0).length) % 7
      year match {
        case x: Int if (x % 4 != 0) => Array.tabulate(12)((month: Int) => (firstDay + (0 to month).map(monthValues).sum) % 7).count(_ == 0)
        case x: Int if (x % 4 == 0 && x % 1000 != 0) => Array.tabulate(12)((month: Int) => (firstDay + (0 to month).map(leapMonthValues).sum) % 7).count(_ == 0)
        case x: Int if (x % 1000 == 0 && x % 400 == 0) => Array.tabulate(12)((month: Int) => (firstDay + (0 to month).map(leapMonthValues).sum) % 7).count(_ == 0)
        case x: Int => Array.tabulate(12)((month: Int) => (firstDay + (0 to month).map(monthValues).sum) % 7).count(_ == 0)
      }
    }
    (1901 to 2000).map(countFirstOfMonthSundays(_)).sum
  }

